wall_max_x = 0
wall_max_y = 0

'''
0 is an empty tile. No game object appears in this tile.
1 is a wall tile. Walls are indestructible barriers.
2 is a block tile. Blocks can be broken by the ball.
3 is a horizontal paddle tile. The paddle is indestructible.
4 is a ball tile. The ball moves diagonally and bounces off objects.
'''
empty = 0
wall = 1
block = 2
pad = 3
ball = 4

pieces = {
    0: ' ',
    1: ' ',
    2: 'X',
    3: '^',
    4: 'O',
}

board = {}

def process_input():
    global g_moves
    global g_og
    '''
    If the joystick is in the neutral position, provide 0.
    If the joystick is tilted to the left, provide -1.
    If the joystick is tilted to the right, provide 1.
    '''
    if len(g_og) != 0:
        move = g_og.pop(0)
        g_moves.append(move)
        return move

    print('(l,r,c) >>> ', end='')
    move = input()

    if move == 'l':
        g_moves.append(-1) 
        return -1
    if move == 'r':
        g_moves.append(1) 
        return 1

    g_moves.append(0) 
    return 0

def redraw():
    global wall_max_x
    global wall_max_y

    for y in range(wall_max_y):
        for x in range(wall_max_x):
            print(pieces[board[(x, y)]], end='')
        print()

def reset_board():
    global g_stockpile
    global g_output

    g_stockpile = []
    g_output = []


def store(register):
    global g_stockpile
    global g_output

    g_stockpile.append(register)
    if len(g_stockpile) == 3:
        g_output.append(Tile(x=g_stockpile[0], y=g_stockpile[1], id=g_stockpile[2]))
        g_stockpile = []

def process_game_state(register):
    global ball_tile
    global pad_tile
    global g_stockpile
    global g_output

    g_stockpile.append(register)
    if len(g_stockpile) == 3:
        current = Tile(x=g_stockpile[0], y=g_stockpile[1], id=g_stockpile[2])
        if current.id == 0:
            pass
        elif current.id == 1:
            pass
        elif current.id == 2:
            pass
        elif current.id == 3:
            board[(pad_tile.x, pad_tile.y)] = 0 
            board[(current.x, current.y)] = 3
            pad_tile = current
            redraw()
        elif current.id == 4:
            if board[(current.x, current.y -1)] == 2:
                board[(current.x, current.y -1)] = 0

            board[(ball_tile.x, ball_tile.y)] = 0 
            board[(current.x, current.y)] = 4
            ball_tile = current
            redraw()
        else:
            print('ggNORE')
            print(current.x, current.y, current.id)

        g_stockpile = []




with open('input') as f:
    puzzle_input = [int(s) for s in f.read().split(',')]

    computer = intcode.IntCode(puzzle_input, observer=store)
    computer.execute()

    empty_tiles = [tile for tile in g_output if tile.id == empty]
    wall_tiles = [tile for tile in g_output if tile.id == wall]
    block_tiles = [tile for tile in g_output if tile.id == block]
    pad_tiles = [tile for tile in g_output if tile.id == pad]
    ball_tiles = [tile for tile in g_output if tile.id == ball]


    wall_max_x = max(wall_tiles, key=lambda t: t.x).x
    wall_max_y = max(wall_tiles, key=lambda t: t.y).y
    board = {}
    for x in range(wall_max_x):
        for y in range(wall_max_y):
            board[(x,y)] = 0

    for tile in block_tiles:
        board[(tile.x, tile.y)] = tile.id
    for tile in wall_tiles:
        board[(tile.x, tile.y)] = tile.id
    for tile in pad_tiles:
        board[(tile.x, tile.y)] = tile.id
    for tile in ball_tiles:
        board[(tile.x, tile.y)] = tile.id

    redraw()

    ball_tile = ball_tiles[0]
    pad_tile = pad_tiles[0]
    reset_board()
    
    print()

    puzzle_input[0] = 2
    computer = intcode.IntCode(puzzle_input, observer=process_game_state, driver=process_input)
    computer.input_register = 0
    computer.execute()
    print(g_moves)
    print('game over')
